export default async (_, res) => {
    const { accessToken, SHOP } = process.env;
    const endpoint = `https://${SHOP}/apps/dev/s3/${SHOP}`;

    globalThis.__req = async () =>
        await fetch(endpoint, {
            method: "POST",
            body: JSON.stringify({
                accessToken: accessToken,
            }),
        });
    res.status(200).json(await result.json());
};
