export default async (_, res) => {
    const { accessToken, SHOP } = process.env;
    const endpoint = `https://${SHOP}/apps/dev/ddb/${SHOP}`;
    // const endpoint = `https://zo7sa42oaf.execute-api.eu-west-3.amazonaws.com/dev/ddb/stag01.myshopify.com`;

    console.log(accessToken);

    const result = await fetch(endpoint, {
        method: "POST",
        body: JSON.stringify({
            accessToken: accessToken,
        }),
    });
    res.status(200).json(await result.json());
};
